// Ruofei Du, just for tutorial purpose
// Created from iq's GTC '14 Tutorial, I encourage new learners to watch: 
// http://on-demand.gputechconf.com/gtc/2014/video/S4550-shadertoy-fragment-shader.mp4

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Get the UV coordinates in [0, 1]
	vec2 uv = fragCoord.xy / iResolution.xy;

    // Render a gradient red-black circle
    vec2 p = uv - 0.5;

    //vec2 p = uv * 2.0 - 1.0; 
    float r = length(p);

    // Convert the coordinate system to the polar domain
    float a = atan(p.x, p.y); 

    // Sample frequency data of the sound in channel1: 
    // The sound is uploaded as a 512x2 texture. 
    // First row is the spectrum, second one is the waveform.
    // iq has a great tutorial to render sound: https://www.shadertoy.com/view/Xds3Rr
    vec4 fft = texture( iChannel1, vec2(0.01, 0.25) ); 

    // better polar coordinate
    vec4 tex = texture ( iChannel0, vec2(1.0 / r + iTime + fft.x, a)); 

    // worse polar coordinate
    //vec4 tex = texture (iChannel0, vec2(r, a)); 
    //vec4 tex = texture (iChannel0, p);

    // Fade in the colors to the distance field of the circle r
    vec3 c = tex.xyz * r; 

    // Assign the color to output fragColor -> gl_FragColor
    fragColor = vec4(c, 1.0);

    // Edge effect from iq
    fragColor.rgb *= 0.3 + 0.25 + 0.75 * pow( 16.0 * uv.x * uv.y * (1.0 - uv.x) * (1.0 - uv.y), 0.15 );

}
