#define SMOOTHING 2.0

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{  
    vec2 center = (iResolution.xy * 0.5);
    float r = min(iResolution.x, iResolution.y) / 2.0;
    float coordDist = length(fragCoord - center);  
   	float stepValue = smoothstep(r, r - SMOOTHING, coordDist);

    vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = vec4(vec3(uv, 0.5+0.5*sin(iTime)) * stepValue, 1.0);
}
