vec2 rand2(vec2 uv)
{
    uv += 0.5;
    uv /= 256.0; // texel size
	return vec2(texture(iChannel0, uv).r, texture(iChannel0, uv + 17.0 / 256.0).r);
}

float voronoi(vec2 uv)
{
    vec2 centerCell = floor(uv);
    float closestDistance = 1000.0;

    for (float y = -1.0; y <= 1.0; y += 1.0)
    {
        for (float x = -1.0; x <= 1.0; x += 1.0)
        {
            vec2 cell = centerCell + vec2(x, y);
            vec2 point = cell + rand2(cell);

            float dist = distance(point, uv);
            closestDistance = min(dist, closestDistance);
        }
    }

    return closestDistance;
}

