{
	vec2 uv = 6.0 * (fragCoord.xy) / iResolution.xy - 3.0;
    float k;
    //int h = 200+int(100.0*sin(iTime));
    for(int i=0;i<1000;++i){
    	uv = vec2(uv.x*uv.x-uv.y*uv.y,2.0*uv.x*uv.y) + 6.0 * (fragCoord.xy) / iResolution.xy - 3.0;
        if(norm(uv) > 2.0){
            k = float(i);
        	break;
        }
    }
    //fragColor = vec4(k,0,k,1);
	fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
}
