vec3 hash3(in vec3 p) {
    vec3 q = vec3(dot(p, vec3(127.1, 311.7, 189.2)),
                  dot(p, vec3(269.5, 183.3, 324.7)),
                  dot(p, vec3(419.2, 371.9, 128.5)));
    return fract(sin(q) * 43758.5453);
}

float noise(in vec3 x, float v) {
    // adapted from IQ's 2d voronoise:
    // https://iquilezles.org/articles/voronoise
    vec3 p = floor(x);
    vec3 f = fract(x);

    float s = 1.0 + 31.0 * v;
    float va = 0.0;
    float wt = 0.0;
    for (int k=-2; k<=1; k++)
    for (int j=-2; j<=1; j++)
    for (int i=-2; i<=1; i++) {
        vec3 g = vec3(float(i), float(j), float(k));
        vec3 o = hash3(p + g);
        vec3 r = g - f + o + 0.5;
        float d = dot(r, r);
        float w = pow(1.0 - smoothstep(0.0, 1.414, sqrt(d)), s);
        va += o.z * w;
        wt += w;
     }
     return va / wt;
}


float fBm(in vec3 p, float v) {
    float sum = 0.0;
    float amp = 1.0;
    for(int i = 0; i < 4; i++) {
        sum += amp * noise(p, v);
        amp *= 0.5;
        p *= 2.0;
    }
    return sum;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
    vec3 rd = normalize(vec3(p.xy, 1.0));
    vec3 pos = vec3(0.0, 0.0, 1.0) * iTime + rd * 10.0;

    vec3 col = vec3(0.0);
    if (p.x < -0.005) {
        col = vec3(0.5 * fBm(pos, 1.0));
    } else {
        col = vec3(0.5 * fBm(pos, 0.2));
    }

    // the black line
    if (p.x > -0.005 && p.x < 0.005 ) {
        col = vec3(0.0);
    }

    fragColor = vec4(col, 1.0);
}
