float pi = 3.1415;






void mainImage( out vec4 color, in vec2 pixCoord )



{
	float zoom = (iResolution.x / 10.0) * (1.0 + 0.2 * cos(2.0 * iTime));
    float cameraX = 5.0   + 4.0 * cos(iTime);
    float cameraY = 2.0  + 4.0 * sin(iTime * 1.1);
    float angle = 20.0*(pi/180.0)+iTime;




    float xBeforeRot = (pixCoord.x / zoom) - cameraX;
    float yBeforeRot = (pixCoord.y / zoom) - cameraY;
    float x = xBeforeRot * cos (angle) + yBeforeRot*sin(angle);
    float y = -xBeforeRot * sin(angle) + yBeforeRot*cos(angle);





    float hauteurMoyenne = 0.0;
    float amplitude = cos(iTime);
    float phase = 4.0 + 5.0 * cos(iTime);
    float periode = 3.0;
    float fx = hauteurMoyenne + cos(x * 2.0 * pi / periode + phase) * amplitude;

    if (y < fx) {
        color = vec4(cos(iTime)*0.5, sin(iTime)*0.3, cos(iTime), 1.0);
        return;
    }

    float fx2 = 1.0 + cos(2.5 * (x + iTime)) *sin(iTime);

    if (y < fx2) {
        color = vec4(0.6+sin(iTime), cos(iTime), 1.0, 1.0);
        return;
    }


    color = vec4(0.0, 0.0, 0.0, 1.0);
}
