void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	//Cercle vert
  	float decalX = iResolution.x/2.0;
    float decalY = iResolution.y/2.0;

    float newX = fragCoord.x - decalX;
    float newY = fragCoord.y - decalY;

    float dist = sqrt(newX*newX+newY*newY);
    //cercle vert droite   
    float decalX2 = decalX +40.0;
    float decalY2 =  decalY + 30.0;

    float newX2 = fragCoord.x - decalX2;
    float newY2 = fragCoord.y - decalY2;

    float dist2 = sqrt(newX2*newX2 + newY2*newY2);

    //cercle vert gauche
    float decalX3 = decalX -40.0;
    float decalY3 =  decalY + 30.0;

    float newX3 = fragCoord.x - decalX3;
    float newY3 = fragCoord.y - decalY3;

    float dist3 = sqrt(newX3*newX3 + newY3*newY3);

    //centre oeil droit
    float decalX4 = decalX2 + 4.0 * cos(5.0 * iTime);
    float decalY4 = decalY2;

    float newX4 = (fragCoord.x - decalX4)/3.0;
    float newY4 = fragCoord.y - decalY4;

    float dist4 = sqrt (newX4*newX4+newY4*newY4);

    //centre oeil gauche
    float decalX5 = decalX3 + 4.0 * cos(5.0 * iTime);
    float decalY5 = decalY3;

    float newX5 = (fragCoord.x - decalX5)/3.0;
    float newY5 = fragCoord.y - decalY5;

    float dist5 = sqrt (newX5*newX5+newY5*newY5);

    //ventre

    float decalX6 = decalX;
    float decalY6 = decalY-70.0;

    float newX6 = fragCoord.x - decalX6;
    float newY6 = fragCoord.y - decalY6;

    float dist6 = sqrt (newX6*newX6+newY6*newY6);

    //pattes haut

    float decalX7 = decalX6+70.0;
    float decalY7 = decalY6+30.0+5.0 * cos(5.0 * iTime);

    float newX7 = fragCoord.x - decalX7;
    float newY7 = fragCoord.y - decalY7;

    float dist7 = sqrt (newX7*newX7+newY7*newY7);

    float decalX8 = decalX6-70.0;
    float decalY8 = decalY6+30.0+5.0 * cos(5.0 * iTime);

    float newX8 = fragCoord.x - decalX8;
    float newY8 = fragCoord.y - decalY8;

    float dist8 = sqrt (newX8*newX8+newY8*newY8);

    //pattes bas

    float decalX9 = decalX6-70.0+5.0 * cos(5.0 * iTime);
    float decalY9 = decalY6-70.0;

    float newX9 = fragCoord.x - decalX9;
    float newY9 = fragCoord.y - decalY9;

    float dist9 = sqrt (newX9*newX9+newY9*newY9);

    float decalX10 = decalX6+70.0+5.0 * cos(5.0 * iTime);
    float decalY10 = decalY6-70.0;

    float newX10 = fragCoord.x - decalX10;
    float newY10 = fragCoord.y - decalY10;

    float dist10 = sqrt (newX10*newX10+newY10*newY10);

    if (uv.y < (1.0/3.0))
    {
        fragColor = vec4(1.0,1.0,0.0,1.0);
    }
    else if (uv.y < (2.0/3.0))
    {
        fragColor = vec4(1.0,0.0,0.0,1.0);
    }
    if (dist6 < 80.0)
    {
        fragColor = vec4(0.2,0.7,0.0,1.0);
    }
    if (dist < 50.0)
    {
        fragColor = vec4(0.6,0.9,0.0,1.0);
    }
        if (dist2 < 25.0)
