void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;


    // テクスチャ画像を表示する.

    float xDirection = 1.0;

    // x座標が0.5 以上のときは左右反転
    if( uv.x > 0.5 ) { xDirection = -1.0; }

    if (uv.y > 0.5 ) {
   		fragColor =  texture(iChannel0, vec2(uv.x * 2.0 * xDirection , uv.y * 2.0));
    } else {
    	fragColor = 1.0 - texture(iChannel0, vec2(uv.x * 2.0 * xDirection , uv.y * 2.0));
    }
}
