#define M_PI 3.141592

// tweaked version of this by 'bmodone' - https://www.shadertoy.com/view/4llcDH

float radial(vec2 uv, float offset, float repeat)
{
    float a = mod((atan(uv.y, uv.x) + M_PI + (offset * 2.0 * M_PI)) * repeat / M_PI, 2.0);
	return min(a,2.0-a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = ( fragCoord - 0.5* iResolution.xy ) / iResolution.y;

    float _d = length(uv); 
 	float d = _d - 0.75;
    float off = _d;
    float a = radial(uv, sin(off*6.0-iTime*0.5)*0.161, 2.0) - 0.9;

    d = off - d*d+d+a*0.32;

    float m = clamp(d, 0.0, 1.0);

    vec3 col = mix(vec3(0.3, 0.1, 0.19), vec3(0.82, 0.31, 0.36), m);

    col *= sin(-off * 32.0 - iTime*4.0) / 3.0 + 1.99;
    col = (col-off*off)+d+a;

    vec3 backg = vec3(0.11,0.10,0.50);

    float m2 = col.r+col.g+col.b*0.3333;
    col = mix(backg,col,m2);
    col *= _d-0.05;

	fragColor = vec4(col,1.0);
}
