void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    //number of rows and columns, can be changed by user
    float numColumns = 5.0;
    float numRows = 4.0;

    //determines the size of each column so they are all equal across the screen
    float columnWidth = 2.0 / numColumns;
    float rowHeight = 2.0 / numRows;

    //vec4s for the two colors/textures, can be modified. Code for  
    vec4 tex1 = texture(iChannel0, uv); //vec4(1.0, 1.0, 1.0, 0.0);
    vec4 tex2 = texture(iChannel1, uv); //vec4(0.0, 0.0, 0.0, 0.0);

	//checks to see if the current x/y position is not on a line, and then 
    if (mod(uv.x, columnWidth) > 0.0 && mod(uv.x, columnWidth) < columnWidth / 2.0) 
    {
        if (mod(uv.y, rowHeight) > 0.0 && mod(uv.y, rowHeight) < rowHeight / 2.0) 
    	{
        	fragColor = tex1;
    	}
    	else
    	{
        	fragColor = tex2;
    	}
    }
    else
    {
        if (mod(uv.y, rowHeight) > rowHeight / 2.0 && mod(uv.y, rowHeight) < rowHeight) 
    	{
        	fragColor = tex1;
    	}
    	else
    	{
        	fragColor = tex2;
    	}
    }
}
