#define MAX_VALUE 4.0
#define MAX_ITERATIONS 150

vec2 complex_add(vec2 left, vec2 right)
{
    return left + right;
}

vec2 complex_mult(vec2 left, vec2 right)
{
    return vec2(
        left.x * right.x - left.y * right.y,
        left.x * right.y + left.y * right.x);
}

float complex_norm(vec2 number)
{
    return dot(number, number);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    int num_iterations= min(int(iTime * 20.0), MAX_ITERATIONS);
    float zoom = 1.0;

    // X:[-2.5, 1.0]
    // Y:[-1.0, 1.0]
    vec2 uv = vec2(
        fragCoord.x / iResolution.x * (3.5 * zoom) - (2.5 * zoom),
        fragCoord.y / iResolution.y * (2.0 * zoom) - (1.0 * zoom));

    vec2 c = uv;
    vec2 z;
    int iteration = 0;

    while (iteration < num_iterations && complex_norm(z) < MAX_VALUE)
    {
        z = complex_add(complex_mult(z, z), c);

        iteration++;
    }

    fragColor = vec4(float(iteration) / float(MAX_ITERATIONS));
}
