// Author: ocb
// Title: On stage

/* 	Playing with grid over texture in buffer A
	Adding lights rays in Main Image
*/

float H2 (in vec2 st) { 						
    return fract(sin(dot(st,vec2(12.9898,8.233))) * 43758.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec3  col = texture(iChannel0, uv).rgb;
    vec2 light = vec2(sin(iTime*.1)+1.5,cos(iTime*.07)+1.5);
    vec2  d = (.3*light-uv)/16.;
    float w = .1;
    vec2  s = uv+d*H2(uv);
    for( int i=0; i<16; i++ )
    {
        col += w*texture(iChannel0, s).rgb;
        w *= .9;
        s += d;
    }

	fragColor = vec4( col, 1.0 );
}

#define I_LIKE_MUSIC

#define T(x) texture(iChannel0,x)

vec2 grid(in vec2 uv, in float x){ uv *= x; return uv - .5*sin(uv);}
vec2 edge(in vec2 x){ return abs(fract(x)-.5);}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float amp = 0.;
    #ifdef I_LIKE_MUSIC
    amp = .6 - texelFetch(iChannel1,ivec2(20,0),0).x + .5*texelFetch(iChannel1,ivec2(400,0),0).x;
    #endif

    float t=iTime*.005;

    vec2 a = grid(uv,10.), b = grid(uv,25.), c = grid(uv,50.);
    vec2 p = floor(a)/10.+t, q = floor(b)/25.+.7*t, s = floor(c)/50.+1.3*t;

    vec2 bp = ( edge(a) + edge(b) + edge(c) )/1.5;
    bp*=bp*bp;

    vec4 tex = .8*T(p)+.6*T(q)+.4*T(s); 
    tex -= .2*(bp.x+bp.y);
    tex *= 2.*smoothstep(1.+amp,1.8,tex.r)*tex;

	fragColor = tex;
}
