#define PI 3.14159
#define TAU 6.28318

//  Function from IÃ±igo Quiles
//  https://www.shadertoy.com/view/MsS3Wc
vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 mouse = vec2(0.5,0.5);
    mouse = iMouse.xy / iResolution.xy;


    float ratio = iResolution.x / iResolution.y;


    float d = distance(vec2(uv.x*ratio,uv.y),vec2(mouse.x*ratio,mouse.y));

    float t = atan((uv.x-mouse.x)*ratio,uv.y-mouse.y);
    t += PI;
    t /= TAU;

    float ld = log(d); //perspective
    //float ld = d*12.; //flat

    float s = mix(t,ld + t,sin(iTime));

    s -= iTime*2.;

    //s = abs(mod(s,1.)-0.5)*2.;
    vec3 c;
    c.r = abs(mod(s+0.1,1.)-0.5)*2.;
    c.g = abs(mod(s,1.)-0.5)*2.;
    c.b = abs(mod(s-0.1,1.)-0.5)*2.;

    c.r = smoothstep(0.47,0.53,c.r);

	fragColor = vec4(c.r,c.g,c.b,1.0);
}
