// Draw Arrow
float leftArrow(vec2 div)
{
    float xx = div.x;
    float xx2 = div.x - 0.5;

    div.y *= 2.0;
    float up = smoothstep(0.0, 0.0025, xx - (div.y - 1.0));
    float down = smoothstep(0.0025, 0.0, 1.0 - xx - div.y);

   	float up2 = smoothstep(0.0, 0.0025, xx2 - (div.y - 1.0));
    float down2 = smoothstep(0.0025, 0.0, 1.0 - xx2 - div.y);

    float o = clamp(up * down, 0.0, 1.0);
    float o2 = clamp(up2 * down2, 0.0, 1.0);

	return o - o2;
}

float rightArrow(vec2 div)
{
    float xx = div.x;
    float xx2 = div.x + 0.5;

    div.y *= 2.0;
    float up = smoothstep(0.0, 0.0025, 1.0 - xx - (div.y - 1.0));
    float down = smoothstep(0.0, 0.0025, div.y - xx);

   	float up2 = smoothstep(0.0, 0.0025, 1.0 - xx2 - (div.y - 1.0));
    float down2 = smoothstep(0.0, 0.0025, div.y - xx2);

    float o = clamp(up * down, 0.0, 1.0);
    float o2 = clamp(up2 * down2, 0.0, 1.0);

	return o - o2;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float divX = 4.0;	// X divition Num
    vec2 divNum = vec2(divX, 1.0);

    vec2 div = fract(uv * divNum);
    float index = floor(uv.x * divNum.x);

    float time = iTime * 1.0;
    float ti = step(0.25, mix(0.0, 1.0, fract(time + index * 0.1)));	// timing
    float ti2 = step(0.25, mix(0.0, 1.0, fract(time - index * 0.1 - 0.2)));	// timing

    float o = leftArrow(div) * ti;
    float o2 = rightArrow(div) * ti2;
    float oo2 = o * o2;

    fragColor = vec4(o - oo2, o2 - oo2, oo2, 1.0);
}
