#define c30     (1.0 / 0.86602540378)	
#define thick   1.
#define PI      3.14159265359
#define grid    20.0
#define timeScale 0.25
#define rt (timeScale * iTime)
#define shadSt  0.6
#define smooth (20./iResolution.x)

float rand(vec3 v){
    return fract(cos(dot(v,vec3(13.46543,67.1132,123.546123)))*43758.5453);
}
float rand(vec2 v){
    return fract(sin(dot(v,vec2(5.11543,71.3132)))*43758.5453);
}

float triGrid(vec2 p, float scale, float sm, float toff){
    float sc = abs(fract((length(p) + rt + toff*rt))-0.5)*2.0;
    float th = thick * sc/2.0;//sin(rt)*sc;
    float stx = (p.x +  c30 / 2.0 * p.y) * scale;
    stx = abs(fract( stx) - 0.5);

    float sty = (p.x -  c30 / 2.0 * p.y) * scale;
    sty = abs(fract( sty) - 0.5);
    float sth = abs(fract(p.y * scale * c30) - 0.5);

    float col = smoothstep(sm, -sm,stx - 0.5 + th);
	col *= smoothstep(sm, -sm,sty - 0.5  + th);
	col *= smoothstep(sm, -sm,sth - 0.5  + th);


    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 st = (gl_FragCoord.xy - 0.5 * iResolution.xy)/iResolution.x;

    vec3 finCol = vec3(triGrid(st,grid,smooth,0.03),
                      triGrid(st,grid,smooth,0.11),
                      triGrid(st,grid,smooth,0.17)
                      );

    fragColor = vec4(finCol,1.0);
}
