//
// Bits of code taken from Inigo Quilez, including fbm(), impulse()
// and friends, sdCone() and friends; also box() by Simon Green.

#define INF 1.0e38
#define HIT(x) hit = min(hit, x)
#define ROOFPOS vec3(0,-1,0.01)

vec4 spherePos[10];

mat2 rot(float angle)
{
	float c = cos(angle);
	float s = sin(angle);
	return mat2(c,-s,s,c);
}
vec3 rot (vec3 v, vec3 axis, vec2 sincosangle)
{
	return v*sincosangle.y + cross(axis, v)*sincosangle.x + axis*(dot(axis, v))*(1.0 - sincosangle.y);
}

float sdSphere (vec3 p, float s)
{
	return length(p)-s;
}


// rd doesn't have to be normalized
float sphere(vec3 ro, vec3 rd, float r)
{
	float b = dot(ro, rd);
	float c = dot(ro, ro) - r * r;
	float a = dot(rd, rd);
	// Exit if râ€™s origin outside s (c > 0) and r pointing away from s (b > 0)
	if (c > 0.0 && b > 0.0)
		return INF;
	float discr = b*b - a*c;
	// A negative discriminant corresponds to ray missing sphere
	if (discr < 0.0)
		return INF;
	// Ray now found to intersect sphere, compute smallest t value of intersection
	float t = - b - sqrt(discr);
	t /= a;
	// If t is negative, ray started inside sphere so clamp t to zero
	t = max(0.0, t);
	return t;
}



float hash (float n)
{
	return fract(sin(n)*43758.5453);
}

float noise (in vec3 x)
{
	vec3 p = floor(x);
	vec3 f = fract(x);

	f = f*f*(3.0-2.0*f);

	float n = p.x + p.y*57.0 + 113.0*p.z;

	float res = mix(mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
						mix( hash(n+ 57.0), hash(n+ 58.0),f.x),f.y),
					mix(mix( hash(n+113.0), hash(n+114.0),f.x),
						mix( hash(n+170.0), hash(n+171.0),f.x),f.y),f.z);
	return res;
}

float box(vec3 org, vec3 dir, vec3 size, out float far)
{
	// compute intersection of ray with all six bbox planes
	vec3 invR = 1.0 / dir;
	vec3 tbot = invR * (-0.5*size - org);
	vec3 ttop = invR * (0.5*size - org);

	// re-order intersections to find smallest and largest on each axis
	vec3 tmin = min (ttop, tbot);
	vec3 tmax = max (ttop, tbot);

	// find the largest tmin and the smallest tmax
	vec2 t0 = max (tmin.xx, tmin.yz);
	float near;
	near = max (t0.x, t0.y);
	t0 = min (tmax.xx, tmax.yz);
	far = min (t0.x, t0.y);

	// check for hit
	return near < far && far > 0.0 ? near : INF;
}

// frustum inscribed in a unit cube centered at 0
#define INF 1.0e38
float frustum(vec3 org, vec3 dir, float apex, vec3 size, out float far)
{
	vec2 dirf = vec2(0.5 - apex, 0.5); 
	vec3 tbot, ttop;

	// intersection with near and far planes
	float invdirx = 1.0 / dir.x;
	tbot.x = invdirx * (-0.5*size.x - org.x);
	ttop.x = invdirx * (0.5*size.x - org.x);

	float temp = dirf.y * (org.x-apex);

	// intersection with inclined planes on y
	tbot.y = size.y * (-temp - dirf.x * org.y) / (dirf.x * dir.y + dirf.y * dir.x);
	ttop.y = size.y * ( temp - dirf.x * org.y) / (dirf.x * dir.y - dirf.y * dir.x);

	// intersection with inclined planes on z
	tbot.z = size.z * (-temp - dirf.x * org.z) / (dirf.x * dir.z + dirf.y * dir.x);
	ttop.z = size.z * ( temp - dirf.x * org.z) / (dirf.x * dir.z - dirf.y * dir.x);

	// if intersecting behind the apex, set t to ray's end
	vec4 tempt = vec4(tbot.yz, ttop.yz);
	tempt = mix(tempt, INF * sign(dir.xxxx), step(org.xxxx + tempt * dir.xxxx, vec4(apex)));
	tbot.yz = tempt.xy;
	ttop.yz = tempt.zw;

	// re-order intersections to find smallest and largest on each axis
	vec3 tmin = min(ttop, tbot);
	vec3 tmax = max(ttop, tbot);

	// find the largest tmin and the smallest tmax
	vec2 t0 = max(tmin.xx, tmin.yz);
	float near = max(t0.x, t0.y);
	t0 = min(tmax.xx, tmax.yz);
	far = min(t0.x, t0.y);

	// check for hit
	return near < far && far > 0.0 ? near : INF;
}

float box(vec3 org, vec3 dir, vec3 size)
{
	float far;
	return box(org, dir, size, far);
}



// Makes brighter near top
float transmittance (vec3 p)
{
    /*vec3 o;
    o.x = sin( iTime ) * .1;
    o.y = sin( iTime * .5) * 2.1;
    o.z = sin( iTime * .05) * 20.1;
	return noise( p * o);*/

    float a = 0.;
    for( int i = 0; i < 10; i++ ){

        vec3 dif = spherePos[i].xyz - p;

       // float n = noise(dif * 5.);
        float l = length( dif );
        a +=  .1 / (l*l);

    }

    return a;

}

float intersect (vec3 ro, vec3 rd)
{
	float hit = INF;

    for( int i = 0; i < 10; i++ ){
		HIT(sphere(ro - spherePos[i].xyz, rd,.1));
    }


	HIT(box (ro - vec3(0.,-1.4,0.), rd, vec3(10.,.01,10.)));
    HIT(box (ro - vec3(0.,-.9,0.), rd, vec3(2.,.05,2.)));

    HIT(box (ro - vec3(4.,0.,0.), rd, vec3(.01,10.,10.)));
    HIT(box (ro - vec3(-4.,0.,0.), rd, vec3(.01,10.,10.)));
    HIT(box (ro - vec3(0.,0.,4.), rd, vec3(10.,10.,.01)));
    HIT(box (ro - vec3(0.,0.,-4.), rd, vec3(10.,10.,.01)));
    //HIT(box (ro - vec3(0.,-.8,0.), rd, vec3(3.,.1,3.)));


	return hit;
}

#define STEPS 20
vec3 inscatter ( vec3 origin, vec3 ro, vec3 rd, vec3 roLight , vec3 rdLight, vec3 lightDir, float hit, vec2 screenPos)
{
	float far;
    //float near = frustum(roLight + vec3(0.0, 1.0, 0.0), rdLight,.01, vec3(1.5, 3.0, 1.5), far);
	float near = box(roLight + origin, rdLight, vec3(1., 30., 1.), far);
	if(near == INF || hit < near)
		return vec3(0);

	float distAlongView = min(hit, far) - near;
	float oneOverSteps = 1.0/float(STEPS);
	vec3 step = rd*distAlongView*oneOverSteps;
	vec3 pos = ro + rd*near;
	float light = 0.0;

	// add noise to the start position to hide banding
	pos += rd*noise(vec3(2.0*screenPos, 0.0))*0.05;

	for(int i = 0; i < STEPS; i++)
	{
		float l = intersect(pos, lightDir) == INF ? 1.0 : 0.0;
		l *= transmittance(pos);
		light += l;

        //include light particles later ?
		//light += particles(pos)*l;
		pos += step;
	}

	light *= oneOverSteps * distAlongView;
	return light*vec3(0.3, .2 , 1. );
}






//----
// Camera Stuffs
//----
mat3 calcLookAtMatrix( in vec3 ro, in vec3 ta, in float roll )
{
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(sin(roll),cos(roll),0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    return mat3( uu, vv, ww );
}

void doCamera( out vec3 camPos, out vec3 camTar, in float time, in float mouseX )
{
    float an = 0.3 + 10.0*mouseX;
	camPos = vec3(3.5*sin(an),1.0,3.5*cos(an));
    camTar = vec3(0.0,0.0,0.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ){

     for( int i =0; i < 10; i++ ){

        float x = 1.4 * cos(iTime *.13 * (float( i )+2.));
        float y = .44 * sin(iTime * .075 * (float( i )+4.));
        float z = 1.4 * sin(iTime * .1 * (float( i )+3.3));
        float r = .00001 * ( sin( iTime * .1  *( float( i) +1.))+2.);
    	spherePos[i] = vec4( x ,  y ,  z , .001 );


    }

    vec2 p = (-iResolution.xy + 2.0*fragCoord.xy)/iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;

    //-----------------------------------------------------
    // camera
    //-----------------------------------------------------

    // camera movement
    vec3 ro, ta;
    doCamera( ro, ta, iTime, m.x );

    // camera matrix
    mat3 camMat = calcLookAtMatrix( ro, ta, 0.0 );  // 0.0 is the camera roll

	// create view ray
	vec3 rd = normalize( camMat * vec3(p.xy,2.0) ); // 2.0 is the lens length


    // raycast the scene
	float hit = intersect(ro,rd);
	vec3 hitPos = ro + hit * rd;

    vec3 col = vec3( 0. );


	// white window
	if (hit == INF)
	{
		col = vec3( 1. , 0. , 0. );


    }else{
        col = vec3( .1 , 0.1 , 0.2 );
    }


    // white window
	if (hit == INF)
	{
		fragColor = vec4(0.0);
		return;
	}

    /*vec3 roLight = vec3( 0. , 4. , 0. );
    vec3 rdLight = vec3( 0. , -1. , 0. );
    vec3 lightDir = normalize( rdLight );

    col += inscatter(ro, rd, roLight, rdLight, lightDir, hit, fragCoord.xy);*/

    // direct light (screw shading!)
	vec3 lightRotAxis = vec3(0.707,0,0.707); //1,0,1 normalized

    float angle = .28; //* sin(iTime);
	vec2 lightAngleSinCos = vec2(sin(angle), cos(angle));
	vec3 lightDir = rot(vec3(0,1,0), lightRotAxis, lightAngleSinCos);
	float shadowBias = 1.0e-4;
	//vec3 col = vec3(0.0);
	if (intersect(hitPos + lightDir*shadowBias, lightDir) == INF)
		col = vec3(0.1,0.2,0.3);

	// inscatter
	lightAngleSinCos.x *= -1.0; // rev angle
	vec3 roLight = rot(ro + ROOFPOS, lightRotAxis, lightAngleSinCos);
	vec3 rdLight = rot(rd, lightRotAxis, lightAngleSinCos);
	col += inscatter(vec3( 0. , 1. , 0.),ro, rd, roLight, rdLight, lightDir, hit, fragCoord.xy);
    col += inscatter(vec3( 2. , 1. , 0.),ro, rd, roLight, rdLight, lightDir, hit, fragCoord.xy);
    col += inscatter(vec3( 0. , 1. , 2.),ro, rd, roLight, rdLight, lightDir, hit, fragCoord.xy);
    col += inscatter(vec3( 0. , 1. , -2.),ro, rd, roLight, rdLight, lightDir, hit, fragCoord.xy);
    col += inscatter(vec3( -2. , 1. , 0.),ro, rd, roLight, rdLight, lightDir, hit, fragCoord.xy);

    //col += inscatter(ro, rd, roLight, rdLight, lightDir, hit, fragCoord.xy);


    vec3 c = col;
    // color correction - Sherlock color palette ;)
	c.r = smoothstep(0.0, 1.0, c.r * 1.5);
	c.g = smoothstep(0.0, 1.0, c.g - 0.0);
	c.b = smoothstep(-0.2, 1.3, c.b);

 	fragColor = vec4( c , 1. );   


}
