void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float u = uv.x;
    float v = uv.y;

    float r = (abs(u-0.5)*2.0);
    if( u > 0.5 )
        r = r*r*r*r*r*r;

    float g = (abs(u-0.5)*2.0);
    g = g*g;
    g = 1.0-g;
    g = sqrt(g)/2.0;

    float b = u;
    b = b*b;

    float a = abs(u-0.5)*2.0;
    a = a*a*a;
    a = 1.0-a;

    r += g/2.0;
    b += g/2.0;

    r *= sin(iTime/2.0)/2.0+0.5;
    g *= sin(iTime/3.0)/2.0+0.5;
    b *= sin(iTime/5.0)/2.0+0.5;

	fragColor = vec4(r,g,b,1.0)*a;

    if( v > fragColor.x/2.0+0.5 )
        fragColor.x = 0.0;

    if( v > fragColor.y/2.0+0.5 )
        fragColor.y = 0.0;

    if( v > fragColor.z/2.0+0.5 )
        fragColor.z = 0.0;

    if( v > 0.48 && v < 0.5 )
        fragColor = vec4(0.0);
}
