float rows = 150.0;
float cols = 150.0;
vec4 white = vec4 (1.0, 1.0, 1.0, 1.0);
vec4 black = vec4 (0.0, 0.0, 0.0, 1.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    

	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 texture1 = texture(iChannel0, uv);
    vec4 texture2 = texture(iChannel1, uv);

    uv.y *= (iResolution.y / iResolution.x);

    rows = (0.5 * sin(iTime) + 0.5) * 10.0;
    cols = (0.5 * cos(iTime) + 0.5) * 10.0;

    float rowLength = floor(uv.x * rows);
    float colLength = floor(uv.y * cols);

   	float sum = rowLength + colLength;

    if(mod(sum, 2.0) == 0.0)
    {
        fragColor = black;
    }
    else
    {
        fragColor = white;
    }

}
