void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 r = (fragCoord.xy) / iResolution.y;
    vec4 mainTex = texture(iChannel0, uv);
    vec2 mouse = iMouse.xy / iResolution.y;

    float radius = 0.3;
    vec3 spherePos = vec3(mouse, 0.0);
    float sphere = 1.0 - smoothstep(radius - 0.001, radius + 0.001, length(r - spherePos.xy));
    vec3 uv3D = vec3(r, 0.0);
    float angle = acos(length(r - spherePos.xy) / radius);
    vec3 sphereHit = uv3D;
    sphereHit.z = sin(angle) * radius;
    vec3 ray = sphereHit - spherePos;
    vec3 camPos = vec3(0.5, 0.5, -5.0);
    vec3 camToSphereHit = sphereHit - camPos;
    float glossiness = 0.05;
    float dotty = pow(dot(camToSphereHit, ray), glossiness);
    uv += dotty;


    mainTex = mix(mainTex, texture(iChannel0, uv), sphere);

    fragColor = mainTex;
}
