float f(vec3 p)
{
    vec3 pp = p*p;
    vec3 ppp = pp *p;
    float val = pp.x + 2.25 * pp.y + pp.z -1.0;
    return val * val * val - pp.x *ppp.z - 0.1125 * pp.y * ppp.z;
}

