vec2 complex_multiply(vec2 a, vec2 b){
	return vec2(a.x*b.x - a.y * b.y, a.x * b.y + a.y * b.x);   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= vec2(0.5,0.5);
    //uv *= vec2(1.0*sin(iTime)+1.2);
    uv *= vec2(exp(-mod(iTime,24.0) * 0.3));
    //uv *= vec2(3.0);
    uv -= vec2(1.4,0);
    //uv += vec2(cos(0.9*iTime)+0.4,sin(iTime)+0.4);
    vec2 z = vec2(0,0);
    for(int i = 0; i < 500; i++){
   		z = complex_multiply(z,z) + uv;
        if(length(z) > 2.0) break;
    }
    if(length(z) > 2.0)
        fragColor = vec4(0.5,0.5,0.5,1.0);
    else
        fragColor = vec4(0,0,0,0);

    //uv = abs(complex_multiply(uv,vec2(sin(iTime*2.0),cos(iTime*2.0))));
	//fragColor = vec4(uv*vec2(0.5),0.0,1.0);
}

