void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 ballPos = vec2(320, 180);
    float dist = distance(ballPos, fragCoord);
    float rot = atan(fragCoord.x - ballPos.x * 2., fragCoord.y * 2. - ballPos.y);

    float size = 0.;

    if(dist < size + sin(tan(iTime) * rot) * 200.)
    {
        fragColor = vec4(vec2(0.7, 0.7),1.0,0.5);
    }
    else
    {
        fragColor = vec4(0.8, 0.8, 1.0, 1.0);
    }
}
