void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float PI = 3.14159265359;
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 color = vec3(
        ceil(sin(2.0 * iTime * floor(cos( uv.y * PI * 40.0)) + PI + uv.x * PI * 20.0)) 
        * ceil(sin(PI + uv.y * PI * 20.0)), 
        0.3,
        0.4
    );
    float shaping = floor(cos(PI + uv.x * PI * 40.0)) - floor(cos( uv.y * PI * 40.0));
	fragColor = vec4(shaping * color,1.0);
}
