vec4 BackgroundLayer = vec4(1.0, 1.0, 1.0, 1.0);
vec4 ClearBackground = vec4(0.0, 0.0, 0.0, 0.0);

//HouseBase
vec4 HouseLayer = vec4(0.0, 0.7, 0.0, 1.0); 
float HouseWidth = 0.3;
float HouseHeight = 0.15;
vec2 HousePosition = vec2(0.5, 0.2);
float ToggleHouseLayer = 1.0;
//chimney
vec4 ChimneyLayer = vec4(1.0, 0.6, 0.0, 1.0);
float ChimneyWidth = 0.05;
float ChimneyHeight =0.1;
vec2 ChimneyPosition = vec2(0.7, 0.38);
float ToggleChimneyLayer = 1.0;
//window

vec4 FirstWindow = vec4(0.5, 1.0, 1.0, 1.0);
vec4 SecondWindow = vec4(0.5, 1.0, 1.0, 1.0);
vec4 WindowLayer;
float WindowSize = 0.05;
vec2 WindowPosition1 = vec2(0.7, 0.2);
vec2 WindowPosition2 = vec2(0.3, 0.2);
float ToggleWindowLayer = 1.0;

//Door
vec4 DoorLayer = vec4(1.0, 0.6, 0.0, 1.0);
float DoorWidth = 0.05;
float DoorHeight = 0.07;
vec2 DoorPosition = vec2(0.5, 0.12);
float ToggleDoorLayer = 1.0;


float createRectangle(vec2 pos, vec2 uv, float height, float width)
{
   float interp = step( length(pos.x- uv.x), width) * step(length(pos.y - uv.y), height);
   return interp; 
}


void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
 vec2 uv = fragCoord.xy / iResolution.xy;

 float aspect = iResolution.y / iResolution.x;
 uv.y *= aspect;


 HouseLayer = HouseLayer * createRectangle(HousePosition, uv, HouseHeight, HouseWidth) * ToggleHouseLayer;
 ChimneyLayer = ChimneyLayer   * createRectangle(ChimneyPosition, uv, ChimneyHeight, ChimneyWidth) * ToggleChimneyLayer;
 FirstWindow = FirstWindow * createRectangle(WindowPosition1, uv, WindowSize, WindowSize) * ToggleWindowLayer;
 SecondWindow = SecondWindow * createRectangle(WindowPosition2, uv, WindowSize, WindowSize) * ToggleWindowLayer;
 DoorLayer = DoorLayer * createRectangle(DoorPosition, uv, DoorHeight, DoorWidth) * ToggleDoorLayer;




 vec4 Composite = mix(BackgroundLayer, ChimneyLayer, ChimneyLayer.a);
    Composite = mix(Composite, HouseLayer, HouseLayer.a);
    Composite = mix(Composite, FirstWindow, FirstWindow.a);
    Composite = mix(Composite, SecondWindow, SecondWindow.a);
    Composite = mix(Composite, DoorLayer, DoorLayer.a);
    fragColor = Composite;
}
