void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;	

    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    //uv.x *= 1. - .1* sin(iTime);

    float d = length (uv);
    float r = 0.33 * (1. - sin(2.*iTime)*0.105);

    float c = smoothstep (r, r - .01, d);
    float isRed = .0;

    if (uv.x > .0) isRed = 1.;

    if (true)//switch to basic required homework
    {
        c *= (0.9 + 0.1*sin(2.*iTime))*(0.76 - 1.66*sin(uv.x)* (1.66 - cos(uv.y)));
        isRed = (1. - 0.3*sin(2.*iTime))*(uv.y * uv.y + .5);
    	if ((uv.x > .0 && uv.y < .0) || (uv.x < .0 && uv.y > .0)) isRed = uv.x * uv.x *1.2;
    } 

    float a = isRed  - c  ;
    float b = 1.-isRed  - c;    

    fragColor = vec4( a , c , b ,1.0);    
}
