#define TWOPI 6.283185307179586476925286766559

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 xy = (fragCoord.xy - 0.5*iResolution.xy)/(min(iResolution.x, iResolution.y));

    float r = length(xy);
    float theta = atan(xy.y, xy.x);

    float MODULUS = 0.5;
    float logr = log(r);
    float modlogr = mod(logr + theta*MODULUS/TWOPI - 0.1*iTime, MODULUS);
    float texr = 0.3*exp(modlogr);

    vec2 uv = vec2(0.5, 0.5) + vec2(texr*cos(theta), texr*sin(theta));

    fragColor = texture(iChannel0, uv);
}
