void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;



    vec2 p = (2.0*fragCoord.xy-iResolution.xy)/min(iResolution.y,iResolution.x);  
    p.y -=0.25;

    vec3 bcol = vec3(1.0,0.8,0.7-0.07*p.y)*(1.0-0.25*length(p));

    float tt = mod(iTime,3.5)/3.5;  
    float ss = pow(tt,.2)*0.5 + 0.5;  
    ss = 1.0 + ss*0.5*sin(tt*6.2831*3.0 + p.y*0.5)*exp(-tt*4.0);  
    p *= vec2(0.5,1.5) + ss*vec2(0.5,-0.5);  


    // shape  
    float a = atan(p.x+0.2,p.y-0.0)/3.141593/1.1;  
    float r = length(p)+0.0;  
    float h = abs(a);  
    float d = (13.0*h - 22.0*h*h + 10.0*h*h*h)/(6.0-5.0*h);  

    // color  
    float s = 0.75 + 0.75*p.x;  
    s *= 1.0-0.25*r;  
    s = 0.5 + 0.6*s;  
    s *= 0.5+0.5*pow( 1.0-clamp(r/d, 0.0, 1.0 ), 0.1 );  
    vec3 hcol = vec3(1.0,0.3,0.3)*s;  

    vec3 col = mix( bcol, hcol, smoothstep( -0.03, 0.03, d-r) );  

    fragColor = vec4(col,1.0);
}
