//Dianna Cornett's layered house assignment, due 09/26/2017 at 11:59 pm

//determine properties of body of house
float houseHeight = 0.2;
float houseWidth = 0.3;
vec2 housePos = vec2(0.5, 0.25);
vec3 houseColor = vec3(1.0, 0.7, 1.0);
//show or hide layer via this value
float houseLayerOn = 1.0;

//determine properties of windows
float windowHeight = 0.05;
float windowWidth = 0.05;
vec2 windowPos1 = vec2(0.3, 0.35);
vec2 windowPos2 = vec2(0.5, 0.35);
vec2 windowPos3 = vec2(0.7, 0.35);
vec3 windowColor = vec3 (0.7, 1.0, 1.0);
//show or hide layer via this value
float windowLayerOn = 1.0;

//determine properties of chimney
float chimHeight = 0.05;
float chimWidth = 0.05;
vec2 chimPos = vec2(0.74, 0.5);
vec3 chimColor = vec3(0.6, 0.3, 0.3);
//show or hide layer via this value
float chimLayerOn = 1.0;

//door
float doorHeight = 0.075;
float doorWidth = 0.05;
vec2 doorPos = vec2(0.5, 0.125);
vec3 doorColor = vec3(0.6, 0.1, 0.7);
//show or hide layer via this value
float doorLayerOn = 1.0;

//shade
float shadeHeight = 0.09;
float shadeWidth = 0.3;
vec2 shadePos = vec2(0.5, 0.14);
vec3 shadeColor = vec3(0.9, 0.4, 0.9);
//show or hide layer via this value
float shadeLayerOn = 1.0;

//support columns
float columnHeight = 0.09;
float columnWidth = 0.03;
vec2 colPos1 = vec2(0.35, 0.14);
vec2 colPos2 = vec2(0.65, 0.14);
vec3 colColor = vec3(1.0, 0.7, 1.0);
//show or hide layer via this value
float colLayerOn = 1.0;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    // aspect ratio

 	uv.y *= iResolution.y / iResolution.x;

    //draw house
    float drawHouse = step(length(uv.x - housePos.x), houseWidth) * step(length(housePos.y - uv.y), houseHeight);

    //draw window
    float drawWindow1 = step(length(uv.x - windowPos1.x), windowWidth) * step(length(uv.y - windowPos1.y), windowHeight);
    float drawWindow2 = step(length(uv.x - windowPos2.x), windowWidth) * step(length(uv.y - windowPos2.y), windowHeight);
    float drawWindow3 = step(length(uv.x - windowPos3.x), windowWidth) * step(length(uv.y - windowPos3.y), windowHeight);
    float drawWindows = drawWindow1 + drawWindow2 + drawWindow3;

    //draw chimney
    float drawChimney = step(length(uv.x - chimPos.x), chimWidth) * step(length(uv.y - chimPos.y), chimHeight);

    //draw door
    float drawDoor = step(length(uv.x - doorPos.x), doorWidth) * step(length(uv.y - doorPos.y), doorHeight);

    //draw shade
	float drawShade = step(length(uv.x - shadePos.x), shadeWidth) * step(length(uv.y - shadePos.y), shadeHeight);

    //draw support columns
    float drawColumn1 = step(length(uv.x - colPos1.x), columnWidth) * step(length(uv.y - colPos1.y), columnHeight);
    float drawColumn2 = step(length(uv.x - colPos2.x), columnWidth) * step(length(uv.y - colPos2.y), columnHeight);
    float drawColumns = drawColumn1 + drawColumn2;

    vec4 Layer1 = vec4(houseColor, drawHouse * houseLayerOn);
    vec4 Layer2 = vec4(windowColor, drawWindows * windowLayerOn);
    vec4 Layer3 = vec4(chimColor, drawChimney * chimLayerOn);
    vec4 Layer4 = vec4(shadeColor, drawShade * shadeLayerOn);
    vec4 Layer5 = vec4(colColor, drawColumns * colLayerOn);
    vec4 Layer6 = vec4(doorColor, drawDoor * doorLayerOn);

    vec4 bg = vec4(0.3, 0.7, 1.0, 1.0);



	vec4 Composite = mix(bg, Layer1, Layer1.a);
    	Composite = mix(Composite, Layer2, Layer2.a);
    	Composite = mix(Composite, Layer3, Layer3.a);
    	Composite = mix(Composite, Layer4, Layer4.a);
    	Composite = mix(Composite, Layer5, Layer5.a);
		Composite = mix(Composite, Layer6, Layer6.a);

	fragColor = Composite;


}
