/*
Waves produced by 3 sources, producing interference patterns.
Gradually zooming out.
*/
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = 2.0 * uv - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float f = iTime;
    float d1 = length(uv - vec2(0.5, -0.1));
    float d2 = length(uv - vec2(-0.5, -0.1));
    float d3 = length(uv - vec2(0., 0.3));
    float s = 3.0;
    float amplitude = sin(f*d1 - iTime*s) + cos(f*d1 - iTime*s) +
                      sin(f*d2 - iTime*s) + cos(f*d2 - iTime*s) +
                      sin(f*d3 - iTime*s) + cos(f*d3 - iTime*s);
    vec3 col = vec3(amplitude, d1*d2, d2*d3);
	fragColor = vec4(col, 1.0);
}
