// colorscheme ///////////////////////////////////////////////////////////////////////////
#define COL_BGND           float(0x26)/255., float(0x26)/255., float(0x26)/255.
#define COL_BORDER         float(0xA5)/255., float(0x6F)/255., float(0xE7)/255.
#define COL_LISSAJOUS      float(0xCC)/255., float(0x52)/255., float(0x7A)/255.
#define COL_GURUMEDITATION float(0xF4)/255., float(0x1E)/255., float(0x04)/255.

#define BORDER_TOP     0.15
#define BORDER_BOTTOM  0.85
#define BORDER_SIZE    0.01

#define PI    3.14159265359
#define TWOPI 6.28318530718

// FX IDs ///////////////////////////////////////////////////////////////////////////////
#define FX_COPPERBARS 1
#define FX_LISSAJOUS  10
#define FX_RASTERBARS 11

#define FX_PIXELIZE 20
#define FX_SCANLINES 21
#define FX_DECRUNCH 22
#define FX_NOISE    23
#define FX_CRANKUP_CONTRAST 24

#define FX_KLOTZAEDER 30
#define FX_BOUNCINGBALL 31

#define FX_SCROLL_ONE 40
#define FX_SCROLL_TWO 41

#define FX_LOGO_CYTAX 50
#define FX_LOGO_ICE 51

#define FX_GURU_MEDITATION 666

// buffer constants //////////////////////////////////////////////////////////////////////
const int LOGO_START_Y      = 1;
const int LOGO_START_HEIGHT = LOGO_START_Y + 32;

const int GURU_START_Y      = LOGO_START_HEIGHT + 1; 
const int GURU_HEIGHT       = GURU_START_Y      + 35;

// timing ////////////////////////////////////////////////////////////////////////////////
const float PLAYBACK_TIME_S = 50.;
float t;

// screen position ///////////////////////////////////////////////////////////////////////
vec2 xy;
vec2 xyC;
vec2 xyF;
vec2 xyS;
vec4 resultColor;

float s;
float bass;

// utility methods ///////////////////////////////////////////////////////////////////////
bool isInBorderlines() {
	if ( ((xy.y>BORDER_BOTTOM-BORDER_SIZE) && (xy.y<BORDER_BOTTOM)) ||
         ((xy.y>BORDER_TOP) && (xy.y<BORDER_TOP+BORDER_SIZE))    ){
    	return true;
    } else {
    	return false;
    }
}

bool isInContentArea() {
    if ( (xy.y<BORDER_BOTTOM-BORDER_SIZE) && (xy.y>BORDER_TOP+BORDER_SIZE) ) {
    	return true;
    } else {
    	return false;
    }
}

float rand(vec2 co){
	return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float frandom(float seed) {
    return fract( sin(iTime/4.+dot(seed, 12.9898))*43758.5453);
}


// FX - background ////////////////////////////////////////////////////////////////
void background(inout vec4 resultColor) {
    if ( isInBorderlines() ) {
        resultColor = vec4(COL_BORDER,1.);
    	return;
    }
    if ( isInContentArea()) {
        float bt    = iTime;
        float split = clamp( 3.*sin(bt*1.5), 0.3, 0.7);
        if (xy.x>split) {
            float y = abs(cos(3.*TWOPI*xy.y + bt));
            float r = clamp(y, 0.2, 0.3);
            resultColor = vec4(r*0.5,r*0.7,r,1.);
            return;
        } else {
            float y = abs(cos(3.*TWOPI*xy.y - bt));
            float r = clamp(y, 0.2, 0.3);
            resultColor = vec4(r*.75,r*.75,r*.75,1.);
            return;
        }
    } else {
        float a;   
        if (xy.y<BORDER_TOP+BORDER_SIZE) {
         	a = sin(-0.04+ xy.y*1.2);  
        } else if (xy.y>BORDER_BOTTOM) {
         	a = sin(1.2 -xy.y*1.24);  
        }
        vec3 c = vec3(COL_BORDER)*a*2.5;
        resultColor = vec4(c,1.0 );
    }
}

void bassTheBackground(inout vec4 resultColor) {
    int tx = int(xy.x*512.0);
	tx  = clamp(tx*4+80,70,80);
	float fft = texelFetch( iChannel3, ivec2(tx,0), 0 ).x;
    bass = clamp(fft,0.1,0.9);
    float a   = smoothstep(0.1, 2.2, length(xyC));
    resultColor -= a * bass*0.85;
}

// FX - pixelize /////////////////////////////////////////////////////////////////////
void pixelize(float effectShowValue, inout vec4 resultColor) {
    if ( isInContentArea() ) {
        float m = 1.-(effectShowValue*0.4);
        if ( mod( (xyF.x-t*35.), 5.0) > 3.){
            resultColor *= m;
        }

        if ( mod( (xyF.y-t*35.), 5.0) > 3.){
            resultColor *= m;
        }
    }

}

// FX - scanlines /////////////////////////////////////////////////////////////////////
void scanlines(float effectShowValue, inout vec4 resultColor) {
    if ( isInContentArea() ) {
        float m = 1.-(effectShowValue*0.4);
        if ( mod( (xyF.y-t*35.), 5.0) > 3.){
            resultColor *= m;
        }
    }
}


// FX - noises ////////////////////////////////////////////////////////////////////////
float _noise(vec2 n) {
	const vec2 d = vec2(0.0, 1.0);
    vec2 b = floor(n), f = smoothstep(vec2(0.0), vec2(1.0), fract(n));
	return mix(mix(rand(b), rand(b + d.yx), f.x), mix(rand(b + d.xy), rand(b + d.yy), f.x), f.y);
}

void noise(float effectShowValue, inout vec4 resultColor) {  
    float c = _noise( vec2(xyF.x + iResolution.x*cos(500.*iTime),xyF.y + iResolution.y*sin(439.*iTime))  );
    vec4 nc = vec4(c,c,c,1.);
	resultColor = mix(resultColor, nc, effectShowValue);
}

// FX - decrunch /////////////////////////////////////////////////////////////////////
void decrunch(float effectShowValue, inout vec4 resultColor) {    
    float ra = 0.2 + 2.5 * frandom(t/1.9738);
    float r  = 0.5 * abs( sin(xy.y*100.*ra));

    float ga = 0.4 + 0.8 * frandom(abs(cos(t/4.9138)) );
    float g  = 0.6 * smoothstep( cos(2.73+xy.y*56.*ga), 0.1, 0.6);

    float ba = 0.4 + 0.8 * frandom(exp(cos(iTime/15.)) );
    float b  = 0.6 * smoothstep( sin(0.73+xy.y*66.*ba), 0., 0.7);

    vec3 color = vec3( r, g, b);
    resultColor = vec4(color,1.);   
}


// FX - contrast /////////////////////////////////////////////////////////////////////
void crankUpContrast(float effectShowValue, inout vec4 resultColor) {
    float m = 1.+(effectShowValue * 3.);
	resultColor = vec4(resultColor.xyz*m, m);
}

// FX - rasterbars ///////////////////////////////////////////////////////////////////
float ysMin =BORDER_TOP + 0.12;
float barWidth = 0.02;
void rasterbars(float effectShowValue, inout vec4 resultColor) {    
    vec3 result;
    vec4 barColor;
    vec3 bar = vec3(0.5,ysMin,barWidth);
	// float foo = 0.;
    float foo = xy.y*0.28*-sin(t);
	float xPos = -1. + 1.9*sin(effectShowValue);
    for(int i=0;i<32;i++) {
        // bar.x = xPos;
        bar.x = foo+xPos + 0.22 * sin(t + (float(i) *0.14) );
        if ( ((xy.x+foo)>(bar.x-bar.z-foo)) && ( (xy.x+foo)<(bar.x+bar.z-foo)) ) {
            bar.y =  ysMin + ( float(i) * 0.015);
            if ( (xy.y>bar.y) && (xy.y<(BORDER_BOTTOM-BORDER_SIZE))) {
                float r =  abs(cos( ((bar.x-foo*2.2)-xy.x-0.36)/barWidth));
    			barColor = vec4( vec3(r), (effectShowValue*0.2) );
            }
        }

    }
    resultColor = mix(resultColor,barColor,barColor.a); 
}


// FX - lissajous /////////////////////////////////////////////////////////////////
const float r = 0.03;

const float x1    = 0.63;
const float y1    = 0.5;

const float x1a   = 0.13;
const float x2a   = 0.15;
const float x1f   = 0.17;
const float x2f   = 0.32;
const float x1phi = 6.9;
const float x2phi = 1.;

const float y1a   = 0.19;
const float y2a   = 0.19;
const float y1f   = 0.33;
const float y2f   = 0.13;
const float y2phi = 1.3;
const float y1phi = 3.;

float circle(vec2 r, vec2 center, float radius) {
	float distanceFromCenter = length(r-center);
	float outsideOfDisk = smoothstep( radius-.012, radius+.012, distanceFromCenter);
	float insideOfDisk = 1.0 - outsideOfDisk;
	return insideOfDisk;
}

void lissajous(float effectShowValue, inout vec4 resultColor) {
    float dist;

    float n = floor( effectShowValue * 40.);
    for(float i=0.0;i<n;i+=1.0){
        vec2 pos = vec2(
            x1 + (x1a * sin(i*x1f+iTime+x1phi) ) + (x2a * sin(i*x2f+iTime+x2phi) ),
            y1 + (y1a * cos(i*y1f+iTime+y1phi) ) + (y2a * sin(i*y2f+iTime+y2phi) )
        );
        dist = circle(xy, pos, r * (1.-(.3*sin(i+t*3.))) );
        if ( (xy.y>(pos.y-dist)) && (xy.y<(pos.y+dist)) ) {
        	resultColor -= vec4( mix(vec3(0.,0.,0.), resultColor.rgb, dist), 1.);
        }

    }
}

// FX - cytax logo movement //////////////////////////////////////////////////////////////////
void moveCytaxLogo(float effectShowValue, inout vec4 fragColor) {

    vec2 pos = vec2(.5, 0.9 );
    pos.x +=  cos(iTime*2.1) *0.36 + sin(iTime*1.6) *0.25;
    pos.x +=  (exp(-effectShowValue*8.));
    float scale  = 1.4 * s;

    vec2 targetSize = vec2(31.*8.,25.);
    vec2 targetPos  = vec2( 0.   ,7);
    vec2 tmp = iResolution.xy / targetSize;

    vec2 windowOffset = floor((iResolution.xy - targetSize*scale)*vec2(pos));
    vec2 coord = (xyF- windowOffset) / scale;

    if(coord.x < targetPos.x || coord.y < targetPos.y  || coord.x >= (targetSize.x+targetPos.x) || coord.y >= (targetSize.y+targetPos.y))
    {
        return;
    }

    vec4 col = texture(iChannel0, coord / iChannelResolution[0].xy) ;
    fragColor = vec4( mix(fragColor.rgb, col.rgb, col.a), 1.0);
}

// FX - letÂ´s meditate  //////////////////////////////////////////////////////////////////////
void meditate(float ttt, inout vec4 resultColor) {
    if (ttt<0.5) {
    	resultColor = vec4(0.8,0.8,0.8,1.);
    } else if (ttt>=0.5 && ttt<1.) {
    	resultColor = vec4(0.4,0.4,0.4,1.);
    } else {
        vec4 bCol = vec4(0.,0.,0.,0.);
        float modt = mod(ttt, 2.);
        if (modt<=1.) {
             if (  ((xy.y>0.170) && (xy.y<0.185)) || (xy.y<0.015) ||
                	( ((xy.x<0.01) || (xy.x>0.99)) && (xy.y<0.185))
                ) {
                 bCol = vec4( COL_GURUMEDITATION, 1.0); 
             }
        }

        float scale  = s;
    	vec2 pos = vec2(0.5,0.86);
        vec2 targetSize = vec2(56.*8.,35.);
        vec2 targetPos  = vec2( 0.   ,GURU_START_Y);
        vec2 tmp = iResolution.xy / targetSize;

        vec2 windowOffset = floor((iResolution.xy - targetSize*scale)*vec2(pos));
        vec2 coord = (xyF- windowOffset) / scale;
        if(coord.x < targetPos.x || coord.y < targetPos.y  || coord.x >= (targetSize.x+targetPos.x) || coord.y >= (targetSize.y+targetPos.y))
        {
            resultColor = bCol;
            return;
        } else {
        	resultColor = texture(iChannel0, coord / iChannelResolution[0].xy) ;
        }
    }
}

// FX - DDD - klotzaeder /////////////////////////////////
const vec3 camera = vec3(0.,0.,-12.);
const vec3 target = vec3(0.,0.,0.);

const float fov = 1.5;
const float raymarchMaximumDistance = 40.;
const float raymarchPrecision = 0.001;
const int   raymarchSteps = 50;

vec3 color0 = vec3( 0.4,0.5,0.7 );
vec3 color1 = vec3(0.1, 0.1, 0.3);
vec3 light0 = vec3(-0.5, 0.75, -0.5);
vec3 light1 = vec3(0.5, -0.75, 0.5);
float kt;
vec2 rpos;

vec3 RotateX(vec3 v, float rad)
{
  float cos = cos(rad);
  float sin = sin(rad);
  return vec3(v.x, cos * v.y + sin * v.z, -sin * v.y + cos * v.z);
}
vec3 RotateY(vec3 v, float rad)
{
  float cos = cos(rad);
  float sin = sin(rad);
  return vec3(cos * v.x - sin * v.z, v.y, sin * v.x + cos * v.z);
}
vec3 RotateZ(vec3 v, float rad)
{
  float cos = cos(rad);
  float sin = sin(rad);
  return vec3(cos * v.x + sin * v.y, -sin * v.x + cos * v.y, v.z);
}

mat3 rotationMatrix3(vec3 axis, float angle)
{
    axis = normalize(axis);
    float s = sin(angle);
    float c = cos(angle);
    float oc = 1.0 - c;

    return mat3(oc * axis.x * axis.x + c,           oc * axis.x * axis.y - axis.z * s,  oc * axis.z * axis.x + axis.y * s,
                oc * axis.x * axis.y + axis.z * s,  oc * axis.y * axis.y + c,           oc * axis.y * axis.z - axis.x * s,
                oc * axis.z * axis.x - axis.y * s,  oc * axis.y * axis.z + axis.x * s,  oc * axis.z * axis.z + c          
               );
}

mat3 calcLookAtMatrix(vec3 origin, vec3 target, float roll) {
  vec3 rr = vec3(sin(roll), cos(roll), 0.0);
  vec3 ww = normalize(target - origin);
  vec3 uu = normalize(cross(ww, rr));
  vec3 vv = normalize(cross(uu, ww));
  return mat3(uu, vv, ww);
}

// operations
vec3 twist( vec3 pos, float amount )
{
    vec3 p = normalize( pos );
    float c = cos(amount * p.y);
    float s = sin(amount * p.y);
    mat2  m = mat2(c,-s,s,c);
    vec3  q = vec3(m*pos.xz,pos.y);
    return q;
}

vec2 subtract(vec2 a, vec2 b){ 
    return vec2(max(-a.x, b.x),1.); 
}
vec2 smin( vec2 a, vec2 b, float k ) { 
    float h = clamp( 0.5+0.5*(b.x-a.x)/k, 0.0, 1.0 ); 
    return vec2( mix( b.x, a.x, h ) - k*h*(1.0-h), 1. ); 
}

//primitives
vec2 torus( vec3 p, vec2 radii, vec3 pos, vec4 quat )
{
    mat3 transform = rotationMatrix3( quat.xyz, quat.w );
    vec3 pp = ( p - pos ) * transform;
    float d = length( vec2( length( pp.xz ) - radii.x, pp.y ) ) - radii.y;
    return vec2(d,1.);
}

vec2 sphere( vec3 p, float radius, vec3 pos , vec4 quat)
{
    mat3 transform = rotationMatrix3( quat.xyz, quat.w );
    float d = length( ( p * transform )-pos ) - radius;
    return vec2(d,0.);
}

vec2 roundBox(vec3 p, vec3 size, float corner, vec3 pos, vec4 quat )
{
    mat3 transform = rotationMatrix3( quat.xyz, quat.w );
    return vec2( length( max( abs( ( p-pos ) * transform )-size, 0.0 ) )-corner,0.7);
}

const vec3 pa = vec3(1., 57., 21.);
const vec4 pb = vec4(0., 57., 21., 78.);
float perlin(vec3 p) {
	vec3 i = floor(p);
	vec4 a = dot( i, pa ) + pb;
	vec3 f = cos((p-i)*acos(-1.))*(-.5)+.5;
	a = mix(sin(cos(a)*a),sin(cos(1.+a)*(1.+a)), f.x);
	a.xy = mix(a.xz, a.yw, f.y);
	return mix(a.x, a.y, f.z);
}

vec2 _roundBox(vec3 p, vec3 size, float corner, vec3 pos ) {
    return vec2( length( max( abs(p-pos)-size, 0.0 ) )-corner,0.3);
}

vec2 _cube(vec3 pos, float a, float i, float n) {
	pos = RotateX(  pos, a + (i*PI*2./n+1.));
    pos = RotateZ(  pos, a + (i*PI/n+1.));
    return _roundBox( pos, vec3(2.,2.,2.),  .15, vec3(0.) );
}

float bounceF = 2.5;
const vec3 zero = vec3(0.);
vec2 scene_BouncingBall(vec3 position) {

	float a = kt  * 1.2;

    position.y += 3.8 - 6.4 * clamp( abs(-cos( kt*bounceF)),0.17, 1.);
    position.x *= 0.7 + 0.9 * clamp( abs(-cos( kt*bounceF)),0.  , .33);
    position.y *= 1.25 - 0.75 * clamp( abs(-cos( kt*bounceF)),0.  , .33);

    vec2 sre = sphere( position, 3.3, zero, vec4(0.1) );

    vec4 bxr = vec4( 0., 1., 0., 0. );
    vec3 ptr = position;

    int n = 5;
    vec2 bxs = _cube(ptr,a, 0., float(n) );
    for(int i=1;i<=n;i++) {
        bxs = min(bxs, _cube( position, a, float(i), float(n)) );
    }

    return min( bxs, sre/*subtract(sce, sre)*/ );
}

vec2 scene_Klotzaeder(vec3 position)
{  
    vec4 quat = vec4( 0., 0., cos(  kt) *.9, -3. + kt  * .3 );

    vec3 noise  = position    * 0.22;
         noise += kt * .1;
    float pnoise = 1. + perlin( noise );

    float d = 2.6 + sin(-1.6 + kt *0.8);
    vec2 klotz = roundBox( position, vec3(2.,2.,2.),  .1, zero, quat + vec4( 1., 1., 1., PI / 4. ) );

    float td = 2.0 + sin(-1.7 + kt*0.5);
    vec2 to0 = torus( twist( position, PI * sin( kt ) ), vec2( td,.15), zero, vec4( 1., 0., 0., 0. + kt * .32 ) );
    vec2 to1 = torus( position, vec2( td,.15), zero, vec4( 0., 0., 1., PI *.5 + kt * .3 ) );

    vec2 sre = sphere( position,   d, zero, quat );
    vec2 sce = sphere( position, 1.5, zero, quat ) + perlin( position + kt ) * 0.232;

    return smin( sce, smin( to0, smin( to1, subtract( sre, klotz  ), pnoise ), pnoise ), pnoise);
}

void dddLights_Klotzaeder() {
    color1 = vec3( 0.4,0.5,0.7 );
	color0 = vec3(0.1, 0.1, 0.3);

    light0.y = 2. * ( sin( t * 0.2 ) );
    light0.x = 2. * ( cos( t * 0.1 ) );
	light1.z = 1. * ( -sin( kt * 0.3 ) );

    light1.x = 3.  * (  cos( kt * 0.1 ) );
    light1.z =  .2 * ( -sin( kt * 0.3 ) );
}

void dddLights_BouncingBall() {
    color0 = vec3( 0.4,0.9,0.7 );
	color1 = vec3( COL_BORDER );

    light0.x = -3.1;
    light0.y = 2.1; 
	light0.z = -0.2;


    light1.x = 1.;
    light1.y = 1.1; 
    light1.z = -0.5 +  (.25 + .5 * ( -sin( kt) ));
}

const vec3 v1 = vec3( 1.0,-1.0,-1.0);
const vec3 v2 = vec3(-1.0,-1.0, 1.0);
const vec3 v3 = vec3(-1.0, 1.0,-1.0);
const vec3 v4 = vec3( 1.0, 1.0, 1.0);

vec3 calcNormal_Klotzaeder(vec3 pos, float eps) {
  return normalize( 
      v1 * scene_Klotzaeder( pos + v1*eps ).x +
      v2 * scene_Klotzaeder( pos + v2*eps ).x +
      v3 * scene_Klotzaeder( pos + v3*eps ).x +
      v4 * scene_Klotzaeder( pos + v4*eps ).x 
  );
}

vec3 calcNormal_BouncingBall(vec3 pos, float eps) {
  return normalize( 
      v1 * scene_BouncingBall( pos + v1*eps ).x +
      v2 * scene_BouncingBall( pos + v2*eps ).x +
      v3 * scene_BouncingBall( pos + v3*eps ).x +
      v4 * scene_BouncingBall( pos + v4*eps ).x 
  );
}

vec2 raymarching( vec3 rayOrigin, vec3 rayDir, float maxd, float precis, int fxID ) {
    float latest = precis * 2.0;
    float dist   = 0.0;
    float type   = -1.0;
    vec2 result;
    for (int i = 0; i < raymarchSteps; i++) {

        if (latest < precis || dist > maxd) break;

        if (fxID==FX_KLOTZAEDER) {
        	result = scene_Klotzaeder( rayOrigin + rayDir * dist );
        } else if (fxID==FX_BOUNCINGBALL) {
        	result = scene_BouncingBall( rayOrigin + rayDir * dist );
        }

        latest = result.x;
        dist  += latest;

        type = result.y;
    }

    vec2 res    = vec2(-1.0, -1.0 );
    if (dist < maxd) { res = vec2( dist, type ); }
    return res;
}

vec3 getRay(mat3 camMat, vec2 screenPos, float lensLength) {
  return normalize(camMat * vec3(screenPos, lensLength));
}

vec3 getRay(vec3 origin, vec3 target, vec2 screenPos, float lensLength) {
  mat3 camMat = calcLookAtMatrix(origin, target, 0.0);
  return getRay(camMat, screenPos, lensLength);
}

void dddObject(float effectShowValue, float timeFX, inout vec4 resultColor, int fxID ) {

    if ( !isInContentArea() ) {
        return;
    }

    kt = timeFX;
    rpos = xyC;
    rpos.x *= iResolution.x / iResolution.y;
    rpos.x +=  1.2 + (exp(-effectShowValue*8.));

    vec3 rayDirection = getRay( camera, target, rpos, fov );
    vec2 collision = raymarching( 
        camera, 
        rayDirection, 
        raymarchMaximumDistance, 
        raymarchPrecision,
        fxID
    );

    if ( collision.x > -0.5)
    {
        vec3 diffuseCol;
        vec3 pos = camera + rayDirection * collision.x;
        vec3 nor;
        vec3 lightColor0;
        vec3 lightColor1;
        if (fxID==FX_KLOTZAEDER) {
            dddLights_Klotzaeder();
            nor = calcNormal_Klotzaeder( pos, 0.002);
            diffuseCol  = vec3( 0.,0.1, 0.1);
            lightColor0 = max( 0.0, dot( normalize( light0 ), nor) ) * color0;
            lightColor1 = max( 0.0, dot( normalize( light1 ), nor) ) * color1;
        } else if (fxID==FX_BOUNCINGBALL) {
            dddLights_BouncingBall();
            nor = calcNormal_BouncingBall( pos, 0.002);
            diffuseCol  = vec3( 0.,0.1, 0.);
            lightColor0 = max( 0.0, dot( normalize( light0 ), nor) ) * color0;
            lightColor1 = max( 0.0, dot( normalize( light1 ), nor) ) * color1;            
        }

        float depth = 1./ log( collision.x );
        resultColor = vec4( ( diffuseCol + lightColor0 + lightColor1 ) * depth, 1. );
    }    
}

