void sinScroller01(float ttt, inout vec4 resultColor) {
	xyS    = xyC/FONT_SIZE;
    xyS.y += 0.5+0.55*sin(xyS.x*.25+t*SIN_SPEED) * (xyS.x);
    float s = 0.5*sin(xyS.x) + 0.2*cos(xyS.x*1.2);
    xyS.x += -13.+mod(ttt*SCROLL_SPEED_1,SCROLL_LEN_1) - s;
    resultColor += TextPart1() * vec4(COL_BORDER, 1.) * xy.x;
}

