    return c;
}

void sinScroller02(float ttt, inout vec4 resultColor) {
	xyS    = xyC/FONT_SIZE;
    xyS.y += 0.5+0.55*sin(xyS.x*.25-t*SIN_SPEED) * (xyS.x);
    float s = 0.4*sin(xyS.x*0.8) - 0.3*cos(xyS.x*1.3);
    xyS.x += -13.+mod(ttt*SCROLL_SPEED_2,SCROLL_LEN_2) - s;
    resultColor += TextPart2() * vec4(COL_BORDER, 1.) * xy.x;
}

void effectExecuter(int effectID, float tStart, float tDuration, inout vec4 resultColor) {
    float timeFrame = mod(iTime, PLAYBACK_TIME_S);
    float tt = -tStart + timeFrame;
    tt = clamp(tt,0.,tDuration);
    float effectShowValue = clamp( (tDuration/2. * sin( (tt*3.1415)/tDuration) ),0.,1.);
    if (effectShowValue<.01) {
    	return;
    } else {
        switch(effectID) {

            case FX_CRANKUP_CONTRAST: crankUpContrast(effectShowValue, resultColor); break;
            case FX_LISSAJOUS:        lissajous(effectShowValue, resultColor); break;
            case FX_RASTERBARS:       rasterbars(effectShowValue, resultColor); break;
            case FX_PIXELIZE:         pixelize(effectShowValue, resultColor); break;
            case FX_SCANLINES:        scanlines(effectShowValue, resultColor); break;

            case FX_LOGO_CYTAX:       moveCytaxLogo(tt, resultColor); break;

            case FX_SCROLL_ONE:       sinScroller01(tt, resultColor); break;
            case FX_SCROLL_TWO:       sinScroller02(tt, resultColor); break;

            case FX_BOUNCINGBALL:     dddObject(effectShowValue, tt, resultColor, effectID); break;
            case FX_KLOTZAEDER:       dddObject(effectShowValue, tt, resultColor, effectID); break;    

			case FX_DECRUNCH:         decrunch(effectShowValue, resultColor); break;
            case FX_NOISE:            noise(effectShowValue, resultColor); break;
            case FX_GURU_MEDITATION:  meditate(tt, resultColor); break;


        }
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    xy    = fragCoord.xy/iResolution.xy;
    xyC   = xy*2.-1.;
    xyF   = fragCoord.xy;
	xy.y  = 1. - xy.y;
    s     = iResolution.x/800.;

    t = iTime;

    resultColor  = vec4( vec3(COL_BGND), 1. );
    background(resultColor);
    bassTheBackground(resultColor);

    effectExecuter(FX_DECRUNCH         , 0.,.8, resultColor); 

    effectExecuter(FX_RASTERBARS       ,  1.0,  9.0, resultColor);
	effectExecuter(FX_LISSAJOUS        , 10.0,  9.0, resultColor);
    effectExecuter(FX_PIXELIZE         , 10.0,  9.0, resultColor);

    effectExecuter(FX_RASTERBARS       , 21.0, 12.0, resultColor);
    effectExecuter(FX_SCANLINES        , 21.0, 12.0, resultColor);

    effectExecuter(FX_LISSAJOUS        , 33.0, 12.0, resultColor);
    effectExecuter(FX_PIXELIZE         , 32.0, 12.0, resultColor);


    effectExecuter(FX_LOGO_CYTAX       ,  0.9, PLAYBACK_TIME_S, resultColor);


    effectExecuter(FX_CRANKUP_CONTRAST , 27.3,  .75, resultColor);
    effectExecuter(FX_CRANKUP_CONTRAST , 10.3,  .75, resultColor);
    effectExecuter(FX_CRANKUP_CONTRAST ,  6.5,  .75, resultColor);
    effectExecuter(FX_CRANKUP_CONTRAST ,  2.6,  .75, resultColor);


    effectExecuter(FX_SCROLL_ONE       ,  2.0, 24.0, resultColor); 
    effectExecuter(FX_SCROLL_TWO       , 27.0, 21.0, resultColor);

    effectExecuter(FX_KLOTZAEDER       , 23.0, 20.0, resultColor);
    effectExecuter(FX_BOUNCINGBALL     ,  2.0, 20.0, resultColor);

    effectExecuter(FX_NOISE            ,  5.0,  1.0, resultColor); 
    effectExecuter(FX_NOISE            , 17.0,  1.0, resultColor); 
    effectExecuter(FX_NOISE            , 25.5,  2.5, resultColor); 

    effectExecuter(FX_NOISE            , PLAYBACK_TIME_S-5.8,  3.0, resultColor); 
    effectExecuter(FX_GURU_MEDITATION  , PLAYBACK_TIME_S-4.6 , 4.6, resultColor);


    fragColor   = resultColor; 
}

