const vec2 txStaticDataInited = vec2(9.0,0.0);

float isInside( vec2 p, vec2 c ) { vec2 d = abs(p-0.5-c) - 0.5; return -max(d.x,d.y); }

vec4 loadValue(vec2 re)
{
    return textureLod( iChannel0, (0.5 + re) / iChannelResolution[0].xy, 0.0 );
}

void storeValue(vec2 re, vec4 va, inout vec4 fragColor, vec2 fragCoord)
{
    fragColor = (isInside(fragCoord, re) > 0.0) ? va : fragColor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int x = int(fragCoord.x);
    int y = int(fragCoord.y);

    fragColor = textureLod( iChannel0, fragCoord / iChannelResolution[0].xy, 0.0 );
    vec4 staticDataInited = loadValue(txStaticDataInited);
    if(staticDataInited.x == 0.0)
    {

        if(y >= GURU_START_Y && y < GURU_START_Y + GURU_HEIGHT)
        {
        	drawGuru(fragColor, x, GURU_HEIGHT-y);
        }

        else if(y > LOGO_START_Y && y<LOGO_START_Y + LOGO_START_HEIGHT ) {
            drawCytax(fragColor, x, LOGO_START_HEIGHT-y);
            staticDataInited.x = 1.0;
        } else {
			return;
        }


    } else if(staticDataInited.x == 1.0) {
                fragColor = vec4(abs(sin(iTime)));  
    }

	storeValue(txStaticDataInited, vec4(staticDataInited),	fragColor, fragCoord);
}
