// You can change magic numbers on 8 line
void mainImage(out vec4 c,vec2 u){
	vec2 U = u / iResolution.xy;
    c = texture(iChannel0, U);
    if ((c.g - c.r) * 3. > .5)
        discard;
    U.x = c.g;
    c.g = min(c.g, c.r * .8);
    c += (U.x - c.g) / 2.;}
