vec3 avg(vec3 a, vec3 b)
{
 	return (vec3(a.x + b.x, a.y + b.y, a.z + b.z) * 0.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 col;

    col = vec3(uv.x, 0., 0.);

    col = avg(col, vec3(0., uv.y, 0.));
    //col += avg(col, vec3(0., uv.y, 0.));

	//col = avg(col, vec3(0., 0., 0.));

    if (uv.y + ((mod(uv.x * iTime * 0.05, 1.) * 2. - 1.)) < 0.1)
    {
        col = vec3(1.);

    }

    col *= vec3(0.35, 0., 0.) * fract(uv.x * 250.0);

    col = (mod(fragCoord.x, 2.) == 0.) ? vec3(1., 0., 0.) : vec3(1.);

    mat2x4 quad = mat2x4(
        1., 0., 2., 0.,
        3., 0., 4., 0.
                        );
    quad *= 0.25;

    col =  vec3(quad[0][0], 0., 0.);

    if(uv.x + sin(iTime * 1.0 + uv.y) * 0.1 > 0.25)	col += vec3(0., quad[0][2], 0.);
    if(uv.x + sin(iTime * 1.5 + uv.y) * 0.1 < 0.5)	col += vec3(0., 0., quad[1][0]);
    if(uv.x + sin(iTime * 3.0 + uv.y) * 0.1 > 0.75)	col += vec3(quad[0][0], 0., 0.);

	fragColor = vec4(col,1.0);
}
