#define PI 3.1415972

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 2.0*(fragCoord.xy / min(iResolution.x,iResolution.y) - 0.5*iResolution.xy/ min(iResolution.x,iResolution.y));

    float theta = atan(uv.y, uv.x);
    float theta2 = theta;
    float rho = length(uv);

    float period = 2.*PI/5.;
    theta = mod(theta, period);
    theta = min(theta, period-theta);

    vec2 p = vec2(cos(theta), sin(theta)) * rho;

    float speed = 1.0;

    float alpha = PI*0.3;

    float x = 2.*sin(alpha)/(sin(period*.5)+2.*sin(alpha+period*.5));
    float rad = sin(period*.5)/(sin(period*.5)+2.*sin(alpha+period*.5));

    if(length(p-vec2(1.0-rad, 0.0))<rad || length(p)<1.-rad-rad*cos(alpha) && length(p-x*vec2(cos(.5*period), sin(.5*period)))>rad)
        speed = -1.0;

    float val = sin(100.*rho-iTime*50.*speed);
    val = max(val, sin(100.0 * theta2-iTime*50.*speed)*clamp(rho*10.0 - 2.0, 0.0, 1.0));
    fragColor = vec4(val);

    fragColor *= clamp(rho*10.0 - .5, 0.0, 1.0);

    if(rho < 0.01)
     fragColor = vec4(1.0, 0.0, 0.0, 1.0);
}
