int hash(int x)
{
    x = ((x >> 16) ^ x) * 0x45d9f3b;
    x = ((x >> 16) ^ x) * 0x45d9f3b;
    x = (x >> 16) ^ x;
    return x;
}

float random(int state)
{
    int h = hash(state);
    float result =  intBitsToFloat((0x3F800000 | (h & 0x007fffff))) - 1.0;
    return result;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    int state = int(fragCoord.x) + int(fragCoord.y)*int(iResolution.x);
    float r = random(state);

    fragColor = vec4(r, r, r, 1.0);
}
