void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 v_texCoord=fragCoord.xy/iResolution.xy;

	float steps=40.0*(cos(iTime*0.2)+1.0+0.2);
    vec2 mosaic_size=iResolution.xy/steps;

	vec2 texCoord = v_texCoord * mosaic_size;
    vec2 texCoord_f=fract(texCoord);
	int i=1*int(dot(fract(texCoord),vec2(1,1))>=1.0)+2*int(dot(fract(texCoord),vec2(1,-1))>=0.0);
	texCoord=ceil(texCoord);
	if(i==0)texCoord+=vec2(0,0.5);
	if(i==1)texCoord+=vec2(0.5,1);
	if(i==2)texCoord+=vec2(0.5,0);
	if(i==3)texCoord+=vec2(1,0.5);
	texCoord/=mosaic_size;
    fragColor = texture(iChannel0, texCoord);
}
