// My first Shadertoy experiment
// Algorithm copied from here: http://www.bidouille.org/prog/plasma

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float pi = 3.141592;

    // v1
    float v1 = sin(uv.x*5.0 + iTime);

    // v2
    float v2 = sin(5.0*(uv.x*sin(iTime / 2.0) + uv.y*cos(iTime/3.0)) + iTime);

    // v3
    float cx = uv.x + sin(iTime / 5.0)*5.0;
    float cy = uv.y + sin(iTime / 3.0)*5.0;
    float v3 = sin(sqrt(100.0*(cx*cx + cy*cy)) + iTime);

    float vf = v1 + v2 + v3;
    float r  = cos(vf*pi);
    float g  = sin(vf*pi + 6.0*pi/3.0);
    float b  = cos(vf*pi + 4.0*pi/3.0);

    fragColor = vec4(r, g, b, 1.0);
}
