void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float j = 0.10, k = 0.3;
    float time = iTime * sin(iTime*0.01);
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 p = 1.0-uv;
    time -= distance(2.0+p, uv*2.0);
    j += 1.0-sin(uv.y*k);
    k *= cos(uv.y*k)* time;
    k *= mod(uv.x*j, j*k*sin(time));
    uv += dot(uv.x * j, uv.y * k);
    uv += mod(1.0-p, 1.0-sin(uv));
    uv += reflect(uv.xy, p.yx);
    uv /= mix(uv, uv/p, p*uv);


    fragColor = vec4(uv-0.5, uv.x*uv.y, j*k*uv);
}
