#define V vec3
void mainImage( out vec4 f, in vec2 w )
{
    V c=V(0);
    vec2 p=w/iResolution.xy + .5;
    p=1.-p;

    c= texture(iChannel0,vec2(atan(p.y  ,p.x  )  )).rgb * length(p * 5.) ;
    float of = texture( iChannel1, vec2( .01, .25 ) ).x;
    of *= c.r  * .2  ;
    p +=of; 
//    c *= texture(iChannel0, p).rgb ;
    c *= texture(iChannel0, p + iDate.w * .4).rgb ;


    f=vec4(c, 1.);
}
