void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec3 p = vec3(fragCoord.xy,0.) / iResolution.y;
    float m = mod( iTime * 10., 400. );
    for( float i = 0. ; i < 400. ; ++i ) {
        if ( i > m ) break;
        p = reflect( p, p.yxz ) * 1.01;
    } 
	fragColor=vec4( 1. - p.y * p.y, 1. - p.x * p.x, 1. - p.z * p.z, 0 );
}
