vec4 colorize(float r1)
{
	float r = step(10.,mod(r1, 20.));
	float g = step(20.,mod(r1, 40.));
	float b = step(40.,mod(r1, 80.));
	float a = smoothstep(10., 50.,mod(r1, 60.));
	return vec4(r,g,b,1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	// center circle
	float r1 = length(vec2(0.5,0.5) * iResolution.xy - fragCoord.xy);
	// mouse-movable circle
	float r2 = length(iMouse.xy - fragCoord.xy);

	// make ugly RGB colors
    vec4 c1 = colorize(r1);

    vec4 c2 = colorize(r2);

    vec4 c = clamp(c1+c2, vec4(0.), vec4(1.));

	fragColor = c;
}
