void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = ( 2.0*fragCoord.xy-iResolution.xy )/iResolution.y;
    float MAX_ITERATION = 25.0;
    float mx = iMouse.x/iResolution.x*2.0 - 0.5;
    float my = iMouse.y/iResolution.y*2.0 - 0.5;
    float c_r = mx*sin(iTime*0.5);    
    float c_i = my*cos(iTime*0.5);
    float z_r = uv.x;
    float z_i = uv.y;

    float l;
    float i;
    while ( l < 4.0 && i < MAX_ITERATION )
    { 
        float tmp = z_r;
        z_r = pow( z_r, 2.0 ) - pow( z_i, 2.0 ) + c_r;
        z_i = 2.0*z_i*tmp + c_i;

        l = pow(z_r, 2.0)+pow( z_i, 2.0 );
        i++;
    }

    if ( i < MAX_ITERATION )
    {        
    	fragColor = vec4( i / MAX_ITERATION, 0.0, 0.0, 0.0 ); 
    }
    else
    {
        fragColor = vec4( l*0.1, l*0.2, l, 0.0 ); 
    }
}
