//Fabrice's way :)
void mainImage( out vec4 c, vec2 U )
{
    float t = iTime, a = .1*t;
	U = 10.* ( U -.5 * iResolution.xy ) /iResolution.y
           *  mat2(cos(a),-sin(a),sin(a),cos(a));
    U.x += iTime;
    vec2 p = U; p.x = round(p.x);

    float dis = U.x-p.x,
          f = dot(p,p),
          r = fract(1001.*sin(112.*p.x));
    r = floor(r*10.)/10.-.5;
  	r *= 30.;

    f = sin(f+r*t) * (1.+.2*cos(f+3.*t));
    f = .03+.2*smoothstep(.2,1.,f);

    c -= c;
    c.r = smoothstep(.75*f, f, abs(dis));
}


