//Initialize variables
float checkerRows = 10.0;
float checkerCols = 10.0;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //initialize variables
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec4 texture1 = texture(iChannel0, uv);
    vec4 texture2 = texture(iChannel1, uv);
    vec4 white = vec4(1.0,1.0,1.0,1.0);
    vec4 black = vec4(0.0,0.0,0.0,1.0);
    uv.y *= iResolution.y / iResolution.x;

    //creates checkerboard
    if (mod(uv.x * checkerRows, 2.0)> 1.0 && mod(uv.y * checkerCols, 2.0)>1.0)
    {
    	fragColor = white; 
        //for textures un-comment
        //fragColor = texture1;
    }

    else if(mod(uv.x * checkerRows, 2.0)< 1.0 && mod(uv.y * checkerCols, 2.0)<1.0)
    {
        //for textures un- comment
        //fragColor = texture1;
        fragColor = white;
    }
    else
    {
        fragColor = black;
        //for textures un- comment
        //fragColor = texture2;
    }

}
