void mainImage( out vec4 O, in vec2 U )
{
    vec2 R = iResolution.xy, 
         T = iChannelResolution[0].xy;
    float lod = textureLod(iChannel1, U/R, 0.).r;
    O = fract(iTime/6.) > .5
	    ? textureLod(iChannel0, U/T, 6.* lod -1. )   // example 1: base noise
	    : textureLod(iChannel1, U/R, 6.* lod -1. );  // example 2: self-action on video
}
