#define R iResolution
void mainImage( out vec4 O, in vec2 U )
{
	vec2 uv = (2.*U-R.xy)/R.y;
    uv *= 1.4;

    int I = 64;
    float i_f = float(I);
    float re, im;
    int i;
    int f = iFrame / 100 % 10;

    if(f >= 0 && f <= 3) uv.x-=.5;

    for(i = 1; i < I && re*re+im*im < i_f; i++ )
    {
        float r = re;

        if(f >= 0 && f <= 3)
        {
        	// z*z
        	re = r*r - im*im;
        	im = 2.*r*im;
        }
        else if(f > 3 && f <= 6)
        {
            // z*z*z 
            re = r*r*r - 3.*r*im*im;
        	im = 3.*r*r*im - im*im*im;
        }
        else if(f > 6)
        {
        	// z*z*z*z
        	re = r*r*r*r + im*im*im*im - 6.*r*r*im*im;
    		im = 4.*r*r*r*im - 4.*r*im*im*im;
        }

        re += uv.x;
        im += uv.y;
    }

    if ( i <= I )
    {
        O = vec4((float(i)*2.)/float(I));
    }
}
