const float cfPi = 3.14159;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Position im Bild, die HÃ¶he pos.y geht von 0.0 (unten) bis 1.0 (oben)
    //					 die Breite pos.x geht von 0.0 (links) bis 1.0 (rechts)
	vec2 pos = fragCoord.xy / iResolution.xy;

    // Unsere Position, aber mit Ursprung in der Mitte des Bildes
    vec2 posMid = vec2(-1.0, -1.0) + 2.0 * pos; 
    // HÃ¶he und Breite in VerhÃ¤ltnis setzen
    posMid.x *= iResolution.x / iResolution.y;

    // die Zeit in Sekunden: 1.0 entspricht einer Sekunde
    float fTime = iTime * 1.0;

    float fProgress = smoothstep(0.0, 13.0, fTime);

// a) Bewegung
    float fBackMotion = 0.0;
    fBackMotion += 0.70 * sin(posMid.y * 4.0 + fTime * 02.70) 
         	 	 * sin(posMid.x * 5.0 + fTime * 02.70);

    fBackMotion += 0.10 * sin(posMid.y * 17.0 - fTime * 01.1) 
        	  	  * sin(posMid.x * 19.0 - fTime * 01.3);

    // Null Bewegung oben
    fBackMotion *= sqrt(1.0 - pos.y);
	fBackMotion *= fProgress;

// b) Linien  
    float fValue = 0.0;

    fValue = cos(cfPi * pos.x * 4.0 
                 + 0.06 * atan(fBackMotion * 2.5 + (0.5 * sin(fBackMotion * 2.0))));


	// Schwingung
    float fMod = 2.20 * sin(cfPi * 0.5 + fTime * 5.0 * (1.0 / cfPi));
    fMod *= 1.0 - pos.y;
    fMod *= fProgress;


    fValue = 1.0 - smoothstep(0.1, 0.16, abs(fValue - fMod));
    vec3 col1 = vec3(0.7) + vec3(0.6) * max(-fMod, -0.7);

    fragColor = vec4(col1 * (fValue), 1.0);
}
