vec2 hash22(vec2 p) { 
    float n = sin(dot(p, vec2(4, 5)));
    p = fract(vec2(262145, 262148)/n);
    return cos(p*9.283 + iTime*2.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 tex = texture(iChannel0, uv);
    vec2 noiseUV = hash22(tex.xy);


    //float r = uv.x + 0.2 * uv.y;
    float r = (tex.x * tex.y + noiseUV.x + noiseUV.y)  * 0.6 - 0.2;
	fragColor = vec4(r,r, r, 0.6);
}
